import React from 'react';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import NativeSelect from '@material-ui/core/NativeSelect';
import InputBase from '@material-ui/core/InputBase';

const BootstrapInput = withStyles((theme) => ({
  root: {
    'label + &': {
      marginTop: theme.spacing(3),
    },
  },
  input: {
    borderRadius: 4,
    position: 'relative',
    backgroundColor: theme.palette.background.paper,
    border: '1px solid #ced4da',
    fontSize: 16,
    padding: '10px 26px 10px 12px',
    transition: theme.transitions.create(['border-color', 'box-shadow']),
    // Use the system font instead of the default Roboto font.
    fontFamily: [
      '-apple-system',
      'BlinkMacSystemFont',
      '"Segoe UI"',
      'Roboto',
      '"Helvetica Neue"',
      'Arial',
      'sans-serif',
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"',
    ].join(','),
    '&:focus': {
      borderRadius: 4,
      borderColor: '#80bdff',
      boxShadow: '0 0 0 0.2rem rgba(0,123,255,.25)',
    },
  },
}))(InputBase);

const useStyles = makeStyles((theme) => ({
  margin: {
    margin: theme.spacing(1),
  },
  hashtagsFormControl: {
    margin: theme.spacing(1),
    minWidth: '30%',
  },
  sortByFormControl: {
    margin: theme.spacing(1),
    minWidth: '15%',
  },
  directionFormControl: {
    margin: theme.spacing(1),
    minWidth: '15%',
  },
}));

export default function CustomizedSelects() {
  const classes = useStyles();
  const [hashtags, setHashtags] = React.useState('');
  const [sortBy, setSortBy] = React.useState('likes');
  const [direction, setDirection] = React.useState('asc');

  const handleHashtagsChange = (event) => {
    setHashtags(event.target.value);
  };
  const handleSortByChange = (event) => {
    setSortBy(event.target.value);
  };
  const handleDirectionChange = (event) => {
    setDirection(event.target.value);
  };

  return (
    <>
      {/* Hashtings Input */}
      <FormControl variant="filled" className={classes.hashtagsFormControl}>
        <InputLabel id="demo-simple-select-filled-label">Hashtings</InputLabel>
        <Select
          labelId="demo-simple-select-filled-label"
          id="demo-simple-select-filled"
          value={hashtags}
          onChange={handleHashtagsChange}
          >
          <MenuItem value={10}>Ten</MenuItem>
          <MenuItem value={20}>Twenty</MenuItem>
          <MenuItem value={30}>Thirty</MenuItem>
        </Select>
      </FormControl>
      {/* SortBy Input */}
      <FormControl variant="filled" className={classes.sortByFormControl}>
        <InputLabel id="demo-simple-select-filled-label">SortBy</InputLabel>
        <Select
          labelId="demo-simple-select-filled-label"
          id="demo-simple-select-filled"
          value={sortBy}
          onChange={handleSortByChange}
          >
          <MenuItem value={10}>Post Id</MenuItem>
          <MenuItem value={20}>Author Id</MenuItem>
          <MenuItem value={30}>Likes</MenuItem>
          <MenuItem value={30}>Popularity</MenuItem>
          <MenuItem value={30}>Reads</MenuItem>
        </Select>
      </FormControl>
    {/* SortBy Input */}
    <FormControl variant="filled" className={classes.directionFormControl}>
        <InputLabel id="demo-simple-select-filled-label">Direction</InputLabel>
        <Select
          labelId="demo-simple-select-filled-label"
          id="demo-simple-select-filled"
          value={hashtags}
          onChange={handleDirectionChange}
          >
          <MenuItem value={10}>Ascending</MenuItem>
          <MenuItem value={20}>Descending</MenuItem>
        </Select>
      </FormControl>

    </>
  );
}
