import React from 'react';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import InputBase from '@material-ui/core/InputBase';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

const BootstrapInput = withStyles((theme) => ({
  root: {
    'label + &': {
      marginTop: theme.spacing(3),
    },
  },
  input: {
    borderRadius: 4,
    position: 'relative',
    backgroundColor: theme.palette.background.paper,
    border: '1px solid #ced4da',
    fontSize: 16,
    padding: '10px 26px 10px 12px',
    transition: theme.transitions.create(['border-color', 'box-shadow']),
    // Use the system font instead of the default Roboto font.
    fontFamily: [
      '-apple-system',
      'BlinkMacSystemFont',
      '"Segoe UI"',
      'Roboto',
      '"Helvetica Neue"',
      'Arial',
      'sans-serif',
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"',
    ].join(','),
    '&:focus': {
      borderRadius: 4,
      borderColor: '#80bdff',
      boxShadow: '0 0 0 0.2rem rgba(0,123,255,.25)',
    },
  },
}))(InputBase);

const useStyles = makeStyles((theme) => ({
  margin: {
    margin: theme.spacing(1),
  },
  hashtagsFormControl: {
    margin: theme.spacing(1),
    minWidth: '30%',
  },
  sortByFormControl: {
    margin: theme.spacing(1),
    minWidth: '15%',
  },
  directionFormControl: {
    margin: theme.spacing(1),
    minWidth: '15%',
  },
  searchButton:{
    minWidth: '15%',
    // backgrounColor: '#29BA80'
  }
}));

export default function CustomizedSelects() {
  const classes = useStyles();
  const [hashtags, setHashtags] = React.useState('');
  const [sortBy, setSortBy] = React.useState('likes');
  const [direction, setDirection] = React.useState('asc');

  const handleHashtagsChange = (event) => {
    setHashtags(event.target.value);
  };
  const handleSortByChange = (event) => {
    setSortBy(event.target.value);
  };
  const handleDirectionChange = (event) => {
    setDirection(event.target.value);
  };

  return (
    <>
      {/* Hashtings Input */}
      <FormControl variant="filled" className={classes.hashtagsFormControl}>
        <InputLabel id="demo-simple-select-filled-label">Hashtings</InputLabel>
        <Select
          labelId="demo-simple-select-filled-label"
          id="demo-simple-select-filled"
          value={hashtags}
          onChange={handleHashtagsChange}
        >
          <MenuItem value={"health"}>Health</MenuItem>
          <MenuItem value={"tech"}>Tech</MenuItem>
          <MenuItem value={"startups"}>Startups</MenuItem>
          <MenuItem value={"history"}>History</MenuItem>
          <MenuItem value={"science"}>Science</MenuItem>
          <MenuItem value={"design"}>Design</MenuItem>
          <MenuItem value={"culture"}>Culture</MenuItem>
          <MenuItem value={"politics"}>Politics</MenuItem>
        </Select>
      </FormControl>
      {/* SortBy Input */}
      <FormControl variant="filled" className={classes.sortByFormControl}>
        <InputLabel id="demo-simple-select-filled-label">SortBy</InputLabel>
        <Select
          labelId="demo-simple-select-filled-label"
          id="demo-simple-select-filled"
          value={sortBy}
          onChange={handleSortByChange}
        >
          <MenuItem value={"id"}>Post Id</MenuItem>
          <MenuItem value={"authorId"}>Author Id</MenuItem>
          <MenuItem value={"likes"}>Likes</MenuItem>
          <MenuItem value={"popularity"}>Popularity</MenuItem>
          <MenuItem value={"reads"}>Reads</MenuItem>
        </Select>
      </FormControl>
      {/* SortBy Input */}
      <FormControl variant="filled" className={classes.directionFormControl}>
        <InputLabel id="demo-simple-select-filled-label">Direction</InputLabel>
        <Select
          labelId="demo-simple-select-filled-label"
          id="demo-simple-select-filled"
          value={direction}
          onChange={handleDirectionChange}
        >
          <MenuItem value={"asc"}>Ascending</MenuItem>
          <MenuItem value={"desc"}>Descending</MenuItem>
        </Select>
      </FormControl>
      <Button className={classes.searchButton}><Typography>Search</Typography></Button>

    </>
  );
}
